(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let s;s=setInterval((()=>{let t=(()=>{let t=(new Date("04 july 2022").getTime()-(new Date).getTime())/1e3;return{timeRemainig:t,hours:Math.floor(t/3600),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();t.timeRemainig>0?(t.hours<10?e.textContent="0"+t.hours:e.textContent=t.hours,t.minutes<10?n.textContent="0"+t.minutes:n.textContent=t.minutes,t.seconds<10?o.textContent="0"+t.seconds:o.textContent=t.seconds):t.timeRemainig<=0?(e.textContent="00",n.textContent="00",o.textContent="00"):clearInterval(s)}),1e3)})()})();